INSERT INTO `SERVMASK_PREFIX_posts` VALUES (208,1,'2020-04-18 13:40:10','2020-04-18 13:40:10','<img width=\"698\" height=\"244\" src=\"http://nyuad.ananhany.com/wp-content/uploads/2020/04/dddd-e1587206996670.png\" alt=\"\" srcset=\"http://nyuad.ananhany.com/wp-content/uploads/2020/04/dddd-e1587206996670.png 698w, http://nyuad.ananhany.com/wp-content/uploads/2020/04/dddd-e1587206996670-300x105.png 300w, http://nyuad.ananhany.com/wp-content/uploads/2020/04/dddd-e1587206996670-600x210.png 600w\" sizes=\"(max-width: 698px) 100vw, 698px\" />											\n			<a href=\"#lm\" role=\"button\">\n						Learn More\n					</a>\n			<a href=\"#sub\" role=\"button\">\n						Contact Us\n					</a>\n			<h2>ABOUT HAREEF</h2>		\n		<h4>Hareef is a social enterprise created in April 2020 due to the Covid-19 crisis to help daily wages workers who lost their jobs to learn new skills, make products from home, and participate in initiatives to pass hand by hand this economical and medical crisis!</h4>		\n										<img width=\"698\" height=\"244\" src=\"http://nyuad.ananhany.com/wp-content/uploads/2020/04/dddd-e1587206996670.png\" alt=\"\" srcset=\"http://nyuad.ananhany.com/wp-content/uploads/2020/04/dddd-e1587206996670.png 698w, http://nyuad.ananhany.com/wp-content/uploads/2020/04/dddd-e1587206996670-300x105.png 300w, http://nyuad.ananhany.com/wp-content/uploads/2020/04/dddd-e1587206996670-600x210.png 600w\" sizes=\"(max-width: 698px) 100vw, 698px\" />											\n			<h2>Most Recent Courses</h2>		\n			<a href=\"http://nyuad.ananhany.com/courses/\" role=\"button\">\n						See all courses\n					</a>\n										<img width=\"1024\" height=\"768\" src=\"http://nyuad.ananhany.com/wp-content/uploads/2020/04/5e7bbb87ba85ee1cee49fb76-1024x768.png\" alt=\"\" srcset=\"http://nyuad.ananhany.com/wp-content/uploads/2020/04/5e7bbb87ba85ee1cee49fb76-1024x768.png 1024w, http://nyuad.ananhany.com/wp-content/uploads/2020/04/5e7bbb87ba85ee1cee49fb76-300x225.png 300w, http://nyuad.ananhany.com/wp-content/uploads/2